import React, { Component, Fragment } from "react";



export default class Pokedex extends Component {
  constructor(props) {
    super(props);
    this.listPokemonRender = this.listPokemonRender.bind(this);

    this.onClickPokemonRender = this.onClickPokemonRender.bind(this);
    this.state = {
      lists: [],
      sLoaded :false,
      listsPokemonLoad:[]

    };
  }
  onClickPokemonRender(e,listsPokemonLoad){
    var tets = listsPokemonLoad;
    return (

    <div className="card">
    <img src={listsPokemonLoad.sprites.front_default} className="card-img-top" alt="..." />
    <div className="card-body">
    <h5 className="card-title">{listsPokemonLoad.name}</h5>
      <p className="card-text">
        Some quick example text to build on the card title and make up
        the bulk of the card's content.
      </p>
      <a href="#" className="btn btn-primary">
        Go somewhere
      </a>
    </div>
  </div>
    )
  }


  listPokemonRender() {


    return ( 
   this.state.listsPokemonLoad.map((listsPokemonLoad,index)=>{
        return ( <div className="col-md-4" key={index}  onClick= {(e)=>{ this.onClickPokemonRender(e,listsPokemonLoad); }} >
        <div className="card" >
                  <img src={listsPokemonLoad.sprites.front_default} className="card-img-top" alt="..." />
                 <div className="card-body">
                    <h5 className="card-title">{listsPokemonLoad.name}</h5>
                    <p className="card-text">
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </p>
                    <div className="row">
                   {listsPokemonLoad.types.map((type,index)=>{

                    return ( <div className="col-md-6"> 
                         <a href="#" className="btn btn-primary "> {type.type.name}</a> 
                        {/* <p></p> */}
                      </div>)
                      }
                      )

                      }                     
                    </div>
                  </div>
                </div>
                </div>
                )})
    )

  }
  componentDidMount() {
    // var pokemonData = [{}];

    fetch("https://pokeapi.co/api/v2/pokemon?limit=12")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            sLoaded: true,
            lists: result
          });
          // list of POKEMONS and PROP

          result.results.map((results,index)=>{
            fetch(results.url)
            .then((res) => res.json())
            .then(
              (result) => {
                this.setState ({
                  listsPokemonLoad: [...this.state.listsPokemonLoad, result]
                })              
                },              
              (error) => {
                this.setState({
                  sLoaded: false,
                  error
                });
                console.log("не пішло 2");

              } );            
            })
        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        (error) => {
          this.setState({
            sLoaded: false,
            error
          });
          console.log("не пішло");

        }
      );

  }


  render() {



    return (

      <div className="container">
        <div className="row justify-content-center">
          <div className="pl-5 pr-5">
            <h1>Pokedex</h1>{" "}
          </div>
        </div>
        <div className="row">
          <div className="col-md-8">
            <div className="row">

              <Fragment>
                {this.listPokemonRender()}
              </Fragment>       

            </div>
            <div className="row justify-content-center">
              <a href="#" className="btn btn-primary w-75">
                Load More
              </a>
            </div>
          </div>
          <div className="col-md-4">
            {" "}

            <Fragment>
                {this.onClickPokemonRender}
              </Fragment> 
          </div>
        </div>
      </div>
    );
  }
}
