import React, { Component, Fragment } from "react";

export default class Pokedex extends Component {
  constructor(props) {
    super(props);
    this.listPokemonRender = this.listPokemonRender.bind(this);
    this.state = {
      lists: [],
      sLoaded :false,
      listsPokemonLoad:[]

    };
  }

  listPokemonRender() {
    // return ( 
   this.state.listsPokemonLoad &&  this.state.listsPokemonLoad.map((listResult,index)=>{
        return ( <div className="col-md-4">
        <div className="card"  key={index}>
                  <img src="..." className="card-img-top" alt="..." />
                 <div className="card-body">
                    <h5 className="card-title">{listResult.name}</h5>
                    <p className="card-text">
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </p>
                    <div className="row">
                      <div className="col-md-6">
                        <a href="#" className="btn btn-primary "></a>
                      </div>
                      <div className="col-md-6">
                        <a href="#" className="btn btn-primary "></a>
                      </div>
                    </div>
                  </div>
                </div>
                </div>
                )})
    // )

  }
  componentDidMount() {
    fetch("https://pokeapi.co/api/v2/pokemon?limit=12")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            sLoaded: true,
            lists: result
          });
          // list of POKEMONS and PROP
          result.results.map((results,index)=>{
            fetch(results.url)
            .then((res) => res.json())
            .then(
              (result) => {
                this.setState({
                  sLoaded: true,
                  listsPokemonLoad: result
                });
              },              
              (error) => {
                this.setState({
                  sLoaded: false,
                  error
                });
                console.log("не пішло 2");

              } );

            })




        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        (error) => {
          this.setState({
            sLoaded: false,
            error
          });
          console.log("не пішло");

        }
      );


// if(this.state.sLoaded){


//    })}







  }

  render() {



    return (

      <div className="container">
        <div className="row justify-content-center">
          <div className="pl-5 pr-5">
            <h1>Pokedex</h1>{" "}
          </div>
        </div>
        <div className="row">
          <div className="col-md-8">
            <div className="row">

              <Fragment>
                {this.listPokemonRender()}
              </Fragment>       

            </div>
            <div className="row justify-content-center">
              <a href="#" className="btn btn-primary w-75">
                Load More
              </a>
            </div>
          </div>
          <div className="col-md-4">
            {" "}
            <div className="card">
              <img src="..." className="card-img-top" alt="..." />
              <div className="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </p>
                <a href="#" className="btn btn-primary">
                  Go somewhere
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
